package problem_389

import org.assertj.core.api.Assertions.assertThat
import org.junit.jupiter.api.Test

class SolutionTest {
    @Test
    fun `Description test cases`() {
        val case1 = Solution.findTheDifference("abcd", "abcde")
        assertThat(case1).isEqualTo('e')

        val case2 = Solution.findTheDifference("", "y")
        assertThat(case2).isEqualTo('y')

        val case3 = Solution.findTheDifference("abcde", "rbdeca")
        assertThat(case3).isEqualTo('r')
    }

    @Test
    fun `Edge case 1`() {
        val edgeCase = Solution.findTheDifference("",
            "")
        assertThat(edgeCase).isEqualTo('t')
    }

    @Test
    fun `Edge case 2`() {
        val edgeCase = Solution.findTheDifference("ae", "aea")
        assertThat(edgeCase).isEqualTo('a')
    }
}